import React, { useState } from 'react';

const Dashboard = ({ onLogout }) => {
  const [activeSection, setActiveSection] = useState('Inicio');

  const styles = {
    container: {
      display: 'flex',
      minHeight: '100vh',
      backgroundColor: '#121212',
      color: '#e0e0e0',
      fontFamily: 'Arial, sans-serif',
    },
    sidebar: {
      width: '250px',
      backgroundColor: '#1e1e1e',
      padding: '2rem',
    },
    content: {
      flexGrow: 1,
      padding: '2rem',
    },
    header: {
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      marginBottom: '2rem',
    },
    title: {
      fontSize: '2rem',
      color: '#4fc3f7',
    },
    menuItem: {
      padding: '0.75rem',
      marginBottom: '0.5rem',
      cursor: 'pointer',
      borderRadius: '5px',
      transition: 'background-color 0.3s ease',
    },
    activeMenuItem: {
      backgroundColor: '#4fc3f7',
      color: '#121212',
    },
    logoutButton: {
      backgroundColor: '#f44336',
      color: 'white',
      border: 'none',
      padding: '0.6rem 1.2rem',
      borderRadius: '5px',
      cursor: 'pointer',
      fontSize: '1rem',
      fontWeight: 'bold',
    },
  };

  const menuItems = ['Inicio', 'Franquicias', 'Manuales', 'Clientes', 'Analíticas'];

  const renderContent = () => {
    switch (activeSection) {
      case 'Inicio':
        return (
          <div>
            <h2>Bienvenido al Dashboard de Franmaker</h2>
            <p>Aquí podrás gestionar todas tus franquicias y acceder a análisis detallados.</p>
          </div>
        );
      case 'Franquicias':
        return <h2>Gestión de Franquicias</h2>;
      case 'Manuales':
        return <h2>Creación y Edición de Manuales</h2>;
      case 'Clientes':
        return <h2>Gestión de Clientes</h2>;
      case 'Analíticas':
        return <h2>Análisis de Datos</h2>;
      default:
        return <h2>Seleccione una opción del menú</h2>;
    }
  };

  return (
    <div style={styles.container}>
      <div style={styles.sidebar}>
        {menuItems.map((item) => (
          <div
            key={item}
            style={{
              ...styles.menuItem,
              ...(activeSection === item ? styles.activeMenuItem : {}),
            }}
            onClick={() => setActiveSection(item)}
          >
            {item}
          </div>
        ))}
      </div>
      <div style={styles.content}>
        <header style={styles.header}>
          <h1 style={styles.title}>Dashboard Franmaker</h1>
          <button style={styles.logoutButton} onClick={onLogout}>
            Cerrar Sesión
          </button>
        </header>
        {renderContent()}
      </div>
    </div>
  );
};

export default Dashboard;
