import React, { useState } from 'react';
import { AlertCircle, CheckCircle2, X } from 'lucide-react';
import { Alert, AlertDescription, AlertTitle } from '@/components/ui/alert';

const FranmakerInterface = () => {
  const [showLoginModal, setShowLoginModal] = useState(false);
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [loginMessage, setLoginMessage] = useState('');

  const handleLogin = (e) => {
    e.preventDefault();
    if (email === 'demo@franmaker.com' && password === 'demo123') {
      setLoginMessage('Login exitoso');
    } else {
      setLoginMessage('Credenciales incorrectas');
    }
  };

  const features = [
    { name: 'Dashboard intuitivo', description: 'Visualiza todos tus datos en un solo lugar' },
    { name: 'Creador de manuales', description: 'Crea y actualiza manuales de franquicia fácilmente' },
    { name: 'Gestión de clientes', description: 'Administra tus franquiciados de manera eficiente' },
    { name: 'Análisis de datos con IA', description: 'Obtén insights valiosos con nuestra IA avanzada' }
  ];

  const plans = [
    { name: 'Básico', price: '29', features: ['Dashboard básico', 'Creación de manuales', 'Soporte por email'] },
    { name: 'Pro', price: '59', features: ['Todo lo del plan Básico', 'Gestión avanzada de clientes', 'Análisis de datos con IA', 'Soporte prioritario 24/7'] }
  ];

  return (
    <div className="min-h-screen bg-gradient-to-b from-blue-50 to-white flex flex-col">
      <header className="bg-blue-600 text-white shadow-lg">
        <div className="container mx-auto px-4 py-4 flex justify-between items-center">
          <div className="text-2xl font-bold">Franmaker</div>
          <nav>
            <ul className="flex space-x-6">
              <li><a href="#inicio" className="hover:text-blue-200 transition">Inicio</a></li>
              <li><a href="#caracteristicas" className="hover:text-blue-200 transition">Características</a></li>
              <li><a href="#precios" className="hover:text-blue-200 transition">Precios</a></li>
              <li><button onClick={() => setShowLoginModal(true)} className="bg-white text-blue-600 px-4 py-2 rounded-full hover:bg-blue-100 transition">Login</button></li>
            </ul>
          </nav>
        </div>
      </header>

      <main className="flex-grow container mx-auto px-4 py-12">
        <section id="inicio" className="mb-20 text-center">
          <h1 className="text-5xl font-bold mb-6 text-blue-800">Franmaker: Tu Asistente de Franquicias con IA</h1>
          <p className="text-xl text-gray-600 max-w-2xl mx-auto">Simplifica la gestión de tu franquicia con inteligencia artificial. Optimiza tus operaciones, mejora la comunicación y aumenta tus beneficios.</p>
        </section>

        <section id="caracteristicas" className="mb-20">
          <h2 className="text-3xl font-bold mb-10 text-center text-blue-800">Características Principales</h2>
          <div className="grid md:grid-cols-2 lg:grid-cols-4 gap-8">
            {features.map((feature, index) => (
              <div key={index} className="bg-white p-6 rounded-lg shadow-md hover:shadow-lg transition">
                <h3 className="text-xl font-semibold mb-3 text-blue-600">{feature.name}</h3>
                <p className="text-gray-600">{feature.description}</p>
              </div>
            ))}
          </div>
        </section>

        <section id="precios" className="mb-20">
          <h2 className="text-3xl font-bold mb-10 text-center text-blue-800">Planes de Precios</h2>
          <div className="flex flex-col md:flex-row justify-center gap-8 max-w-5xl mx-auto">
            {plans.map((plan, index) => (
              <div key={index} className="bg-white p-8 rounded-lg shadow-md flex-1 flex flex-col">
                <h3 className="text-2xl font-bold mb-2 text-blue-600">{plan.name}</h3>
                <p className="text-4xl font-bold text-gray-800 mb-6">${plan.price}<span className="text-sm text-gray-500">/mes</span></p>
                <ul className="mb-8 flex-grow">
                  {plan.features.map((feature, fIndex) => (
                    <li key={fIndex} className="mb-3 flex items-center">
                      <CheckCircle2 className="w-5 h-5 mr-2 text-green-500" />
                      <span>{feature}</span>
                    </li>
                  ))}
                </ul>
                <button className="w-full bg-blue-600 text-white px-6 py-3 rounded-full hover:bg-blue-700 transition">Seleccionar Plan</button>
              </div>
            ))}
          </div>
        </section>
      </main>

      <footer className="bg-gray-800 text-white py-8">
        <div className="container mx-auto px-4 text-center">
          <p>&copy; 2024 Franmaker. Todos los derechos reservados.</p>
        </div>
      </footer>

      {showLoginModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
          <div className="bg-white rounded-lg p-8 max-w-md w-full relative">
            <button 
              onClick={() => setShowLoginModal(false)}
              className="absolute top-4 right-4 text-gray-500 hover:text-gray-700"
            >
              <X className="w-6 h-6" />
            </button>
            <h2 className="text-2xl font-bold mb-6 text-blue-800">Acceso Demo</h2>
            <form onSubmit={handleLogin}>
              <div className="mb-4">
                <label htmlFor="email" className="block text-gray-700 mb-2">Email</label>
                <input
                  type="email"
                  id="email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  className="w-full p-3 border border-gray-300 rounded-md focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  required
                />
              </div>
              <div className="mb-6">
                <label htmlFor="password" className="block text-gray-700 mb-2">Contraseña</label>
                <input
                  type="password"
                  id="password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  className="w-full p-3 border border-gray-300 rounded-md focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  required
                />
              </div>
              <button type="submit" className="w-full bg-blue-600 text-white px-4 py-3 rounded-md hover:bg-blue-700 transition">Iniciar sesión</button>
            </form>
            {loginMessage && (
              <Alert className="mt-4" variant={loginMessage === 'Login exitoso' ? 'default' : 'destructive'}>
                <AlertCircle className="h-4 w-4" />
                <AlertTitle>{loginMessage === 'Login exitoso' ? 'Éxito' : 'Error'}</AlertTitle>
                <AlertDescription>
                  {loginMessage}
                </AlertDescription>
              </Alert>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default FranmakerInterface;
